import React, { useState } from 'react'

const BlogForm = ({ handleSubmit }) => {
    const [newTitle, setNewTitle] = useState("")
    const [newAuthor, setNewAuthor] = useState("")
    const [newUrl, setNewUrl] = useState("")
    const [message, setMessage] = useState(null)

    const onSubmit = (event) => {

        event.preventDefault()

        const blogObject = {
            title: newTitle,
            author: newAuthor,
            url: newUrl,
            likes: Math.floor(Math.random() * 200 + 1)
        }

        handleSubmit(blogObject)
        setMessage(`a new blog ${newTitle} by ${newAuthor}`)
        setTimeout(() => {
            setMessage(null)
        }, 3000)
        setNewTitle('')
        setNewAuthor('')
        setNewUrl('')
    }
    return (
        <form onSubmit={onSubmit}>
            <>Title: <input value={newTitle} onChange={({ target }) => setNewTitle(target.value)} /> </>
            <br />
            <>Author: <input value={newAuthor} onChange={({ target }) => setNewAuthor(target.value)} /> </>
            <br />
            <>Url: <input value={newUrl} onChange={({ target }) => setNewUrl(target.value)} /> </>
            <br />
            <button type='submit'> Create </button>
        </form>
    )
}

export default BlogForm
