import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs(blogs)
    )
  }, [])

  const handleLogin = (event) => {
    event.preventDefault()

    console.log('logging in with', username, password)

    try {
      const user = loginService.login({ username, password })
      setUser(user)
      setUsername('')
      setPassword('')
    }
    catch (exception) {
      setErrorMessage("Wrong credentials")
      setTimeout(() => {
        setErrorMessage(null)
      }, 5000)
    }
  }

  return (
    <div>
      <h2>blogs</h2>

      <form onSubmit={handleLogin}>
        <di>
          username <input type="text" value={username} name="username" onChange={(event) => setUsername(event.target.value)} >username goes here</input>
        </di>
        <di>
          password <input type="text" value={password} name="username" onChange={({ target }) => setPassword(target.value)} >password goes here</input>
        </di>
        <button type="submit">login</button>

      </form>


      {blogs.map(blog =>
        <Blog key={blog.id} blog={blog} />
      )}
    </div>
  )
}

export default App
