import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs(blogs)
    )
  }, [])

  const handleLogin = (event) => {
    event.preventDefault()
    console.log('logging in with', username, password)
  }

  return (
    <div>
      <h2>blogs</h2>

      <form onSubmit={handleLogin}>
        <di>
          username <input type="text" value={username} name="username" onChange={(event) => setUsername(event.target.value)} >username goes here</input>
        </di>
        <di>
          password <input type="text" value={password} name="username" onChange={(event) => setPassword(event.target.value)} >username goes here</input>
        </di>

      </form>


      {blogs.map(blog =>
        <Blog key={blog.id} blog={blog} />
      )}
    </div>
  )
}

export default App
