import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'
import Notification from './components/Notification'


const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)
  const [message, setMessage] = useState(null)

  const [newTitle, setNewTitle] = useState("")
  const [newAuthor, setNewAuthor] = useState("")
  const [newUrl, setNewUrl] = useState("")

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs(blogs)
    )
  }, [])

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
    }
  }, [])

  const handleLogin = async (event) => {
    event.preventDefault()
    try {
      const user = await loginService.login({
        username, password,
      })

      window.localStorage.setItem('loggedBlogappUser', JSON.stringify(user))
      blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
    }
    catch (exception) {
      setMessage('Wrong username or password')
      setTimeout(() => {
        setMessage(null)
      }, 5000)
    }
  }

  const handleLogout = () => {
    setUser(null)
    window.localStorage.removeItem('loggedBlogappUser')
  }

  const loginForm = () => (
    <form onSubmit={handleLogin}>
      <h2>log in to application</h2>
      <Notification message={message} />
      <div>
        username
        <input
          type="text"
          value={username}
          name="Username"
          onChange={({ target }) => setUsername(target.value)}
        />
      </div>
      <div>
        password
        <input
          type="password"
          value={password}
          name="Password"
          onChange={({ target }) => setPassword(target.value)}
        />
      </div>
      <button type="submit">login</button>
    </form>
  )


  const addBlog = (event) => {

    event.preventDefault()

    const blogObject = {
      title: newTitle,
      author: newAuthor,
      url: newUrl,
      likes: Math.floor(Math.random() * 200 + 1)
    }

    blogService
      .createNew(blogObject)
      .then(returnedBlog => {
        setBlogs(blogs.concat(returnedBlog))
        setMessage('a new blog `${newTitle}`')
        setTimeout(() => {
          setMessage(null)
        }, 5000)
        setNewTitle('')
        setNewAuthor('')
        setNewUrl('')
      })
  }


  // const blogForm = () => {
  //   <form onSubmit={createBlog}>
  //     <>title <input value={title} onChange={({ target }) => setTitle(target.value)} /> </>
  //     <>author <input value={author} onChange={({ target }) => setAuthor(target.value)} /> </>
  //     <>url <input value={url} onChange={({ target }) => setUrl(target.value)} /> </>
  //   </form>
  // }


  const BlogsList = () => (
    <>
      <h2>blogs</h2>
      <h3> {user.name} logged in</h3>
      <button onClick={handleLogout}>log out</button>

      <h3>create new</h3>
      <form onSubmit={addBlog}>
        <>Title: <input value={newTitle} onChange={({ target }) => setNewTitle(target.value)} /> </>
        <br />
        <>Author: <input value={newAuthor} onChange={({ target }) => setNewAuthor(target.value)} /> </>
        <br />
        <>Url: <input value={newUrl} onChange={({ target }) => setNewUrl(target.value)} /> </>
        <br />
        <button type='submit'> Create </button>
      </form>


      {blogs.map(blog =>
        <Blog key={blog.id} blog={blog} />
      )}


    </>

  )




  return (
    <div>

      {user === null && loginForm()}
      {user !== null && BlogsList()}




      <br />

    </div>
  )
}

export default App
