import React from 'react'
import PropTypes from 'prop-types'
// import { toggleShow, toggleHide } from '../actions'
import { useDispatch, useSelector } from 'react-redux'
import { toggleToShow, toggleToHide } from '../actions'

const Blog = ({ blog, addLikes, removeBlog }) => {
    const blogStyle = {
        paddingTop: 10,
        paddingLeft: 2,
        border: 'solid',
        borderWidth: 1,
        marginBottom: 5,
    }

    const dispatch = useDispatch()
    // const [showDetail, setShowDetail] = useState(false)
    // const toggleDetailsVisibility = () => {
    //     setShowDetail(!showDetail)
    // }

    const toggleDetailsVisibility = () => {
        if (!showDetail) {
            dispatch(toggleShow)
        }
        dispatch(toggleHide)
    }

    const DetailBlog = () => {
        const showDetail = useSelector((state) => state.showDetail)
        return (
            <div
                style={{ display: showDetail ? '' : 'none' }}
                className="fullBlog"
            >
                <i>Url: </i>
                {blog.url}
                <br />
                <i>Likes: </i>
                {blog.likes}{' '}
                <button id="like-button" onClick={addLikes}>
                    like
                </button>
                <br />
                <button id="delete-button" onClick={removeBlog}>
                    Remove
                </button>
            </div>
        )
    }
    return (
        <div style={blogStyle} className="blog">
            <div className="halfBlog">
                <i>Title: </i> {blog.title}{' '}
                <button id="view-button" onClick={toggleDetailsVisibility}>
                    view
                </button>
                <br />
                <i>Author: </i>
                {blog.author}
            </div>
            <DetailBlog />
        </div>
    )
}
Blog.propTypes = {
    removeBlog: PropTypes.func.isRequired,
    addLikes: PropTypes.func.isRequired,
}

export default Blog
