export const blogReducer = (state = [], action) => {
    switch (action.type) {
        case 'TOGGLE_SHOW': {
            const id = action.data.id
            const blogToToggle = state.find((n) => n.id === id)
            const toggledBlog = { ...blogToToggle, showDetail: !showDetail }
        }
        default:
            return state
    }
}
