// const [blogs, setBlogs] = useState([])

import blogService from '../services/blogs'
import { addLike, setBlogs } from '../actions'
export const blogReducer = (state = [], action) => {
    switch (action.type) {
        case 'BLOGS':
            return action.payload
        case 'NEW_BLOG':
            return state.concat(action.payload)
        case 'ADD_LIKE': {
            // const id = action.payload.id
            const blog = action.payload
            const toLikeBlog = state.find((n) => n.id === blog.id)
            const updatedBlog = { ...toLikeBlog, likes: toLikeBlog.likes + 1 }
            return state.map((n) => (n.id !== blog.id ? n : updatedBlog))
        }
        default:
            return state
    }
}

export const initializeBlogs = () => {
    return async (dispatch) => {
        const blogs = await blogService.getAll()
        dispatch(setBlogs(blogs))
    }
}

export const updateLikes = (id) => {
    return async (dispatch) => {
        const toLikeBlog = dispatch(addLike(id))
        await blogService.update(id, toLikeBlog)
        dispatch(setBlogs())
    }
}
