import React, { useState } from "react"
import PropTypes from 'prop-types'


const Blog = ({ blog, addLikes, removeBlog }) => {
  const [blogDetailVisible, setBlogDetailVisible] = useState(false)

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: "solid",
    borderWidth: 1,
    marginBottom: 5
  }

  return (
    <div style={blogStyle}>
      <div style={{ display: blogDetailVisible ? "none" : "" }}>
        <i>Title: </i> {blog.title} <button onClick={() => setBlogDetailVisible(true)}>view</button>
      </div>

      <div style={{ display: blogDetailVisible ? "" : "none" }}>
        <i>Title: </i> {blog.title} <button onClick={() => setBlogDetailVisible(false)}>hide</button>
        <br />
        <i>Author: </i>{blog.author}
        <br />
        <i>Url: </i>{blog.url}
        <br />
        <i>Likes: </i>{blog.likes} <button onClick={addLikes}>like</button>
        <br />
        <button onClick={removeBlog}>Remove</button>
      </div>

    </div>
  )
}
Blog.propTypes = {
  title: PropTypes.string.isRequired,
  url: PropTypes.string.isRequired
}


export default Blog
