export const setMessage = (data) => {
    return { type: 'SET_MESSAGE', payload: data }
}
export const removeMessage = () => {
    return { type: 'REMOVE_MESSAGE' }
}

export const notificationReducer = (state = null, action) => {
    switch (action.type) {
        case 'SET_MESSAGE':
            return action.payload
        case 'REMOVE_MESSAGE':
            return null
        default:
            return state
    }
}
